func isFirstInit(db kvdb.Backend) (bool, error) {
	var metadataExists bool
	err := kvdb.View(db, func(tx kvdb.RTx) error {
		metadataExists = tx.ReadBucket(metadataBkt) != nil
		return nil
	}, func() {
		metadataExists = false
	})
	if err != nil {
		return false, err
	}

	return !metadataExists, nil
}
